import MainContainer from './MainContainer';
import { useState } from 'react';

function App() {
  const [heading, setHeading] = useState('change this');
  const [heading2, setHeading2] = useState('another one')
  function print(message) {
    setHeading(message);
  }

  return (
    <>
      <MainContainer
        title={heading}
        title2={heading2}
        message='string'
        printMessage={print}
      ></MainContainer>
      <h1>{heading}</h1>
    </>
  );
}

export default App;
